package algorithms.q0005;

import org.junit.Before;
import org.junit.Test;

import static org.junit.Assert.*;

public class SolutionTest {

    Solution solution;

    @Before
    public void initialize() {
        solution = new Solution002();
    }

    @Test
    public void testLongestPalindrome_abc() throws Exception {
        String str = "abcba";
        String result = solution.longestPalindrome(str);
        assertEquals(str, result);
    }

    @Test
    public void testLongestPalindrome_b() throws Exception {
        String str = "bb";
        String result = solution.longestPalindrome(str);
        assertEquals(str, result);
    }

    @Test
    public void testLongestPalindrome_ab() throws Exception {
        String str = "ab";
        String result = solution.longestPalindrome(str);
        assertEquals("a", result);
    }


    @Test
    public void testLongestPalindrome_abcd() throws Exception {
        String str = "abdcbca";
        String result = solution.longestPalindrome(str);
        assertEquals("cbc", result);
    }

    @Test
    public void testLongestPalindrome_abadd() throws Exception {
        String str = "abadd";
        String result = solution.longestPalindrome(str);
        assertEquals("aba", result);
    }

    //""
    @Test
    public void testLongestPalindrome_huge() throws Exception {
        long start = System.currentTimeMillis();
        String str = "";
        String result = solution.longestPalindrome(str);
        assertEquals(str, result);
        System.out.println(System.currentTimeMillis() - start);
    }

    //""
    @Test
    public void testLongestPalindrome_huge_2() throws Exception {
        long start = System.currentTimeMillis();
        String str = "";
        String result = solution.longestPalindrome(str);
        assertEquals("", result);
        System.out.println(System.currentTimeMillis() - start);
    }
}